import style from "./Article.module.css";
import { MyEditor } from "../MyEditor";
import { editArticle } from "../../api/apiArticle";
import iconEdit from "../../assets/table/icon-edit.png"
import iconCancel from "../../assets/icon-delete-red.png"
import iconOk from "../../assets/icon-ok-green.png"
import iconAdd from "../../assets/icon-add-green.png"
import iconExcel from "../../assets/excel.png"
import iconWord from "../../assets/word.png"
import iconPdf from "../../assets/pdf.png"
import iconText from "../../assets/text.png"
import iconOther from "../../assets/other.png"
import { useEffect, useState } from "react";
import { createAudio, deleteAudio, getAudios } from "../../api/apiAudio";
import { getVideos, deleteVideo, createVideo } from "../../api/apiVideo";
import { createFile, deleteFile, getFiles } from "../../api/apiFile";
import { Loader } from "../Loader/Loader";


function Article({ admin, article, setArticle }) {

    const [progressVideo, setProgressVideo] = useState(0);      // процент загрузки файлов
    const [loadingVideo, setLoadingVideo] = useState(false);    // отображение лоадера видео

    const [progressAudio, setProgressAudio] = useState(0);      // процент загрузки файлов
    const [loadingAudio, setLoadingAudio] = useState(false);    // отображение лоадера аудио

    const [progressFiles, setProgressFiles] = useState(0);      // процент загрузки файлов
    const [loadingFiles, setLoadingFiles] = useState(false);    // отображение лоадера файлов    

    const [audios, setAudios] = useState([]);                   // массив аудио по статье
    const [videos, setVideos] = useState([]);                   // массив видео по статье
    const [files, setFiles] = useState([]);                     // массив файлов по статье

    const [editName, setEditName] = useState(false);            // открытие названия на редактирование
    const [editContent, setEditContent] = useState(false);      // открытие содержания на редактирование

    const [name, setName] = useState('');                       // название для отрисовки
    const [content, setContent] = useState('');                 // содержание для отрисовки

    // отменяет редактирование названия
    function cancelEditName() {
        setEditName(false);
        setName(article.name);
    };

    // отменяет редактирование содержания
    function cancelEditContent() {
        setEditContent(false);
        setContent(article.content);
    };

    // сохраняет название
    function saveEditName() {
        setEditName(false);
        setArticle(p => { return { ...p, name: name } });
        editArticle(article.id, name, null);
    };

    // сохраняет содержание
    function saveEditContent() {
        setEditContent(false);
        setArticle(p => { return { ...p, content: content } });
        editArticle(article.id, null, content);
    };

    // удаляет видео
    function delVideo(video_id) {
        deleteVideo(video_id).then(response => {
            getVideos(article.id).then(response => setVideos(response.data));
        });
    };

    // загружает видео
    function addVideo() {
        document.querySelectorAll('.input-file-video-hidden').forEach(item => item.remove());

        const input = document.createElement('input');
        input.classList.add('input-file-video-hidden');
        input.accept = 'video/*';
        input.hidden = true;
        input.type = 'file';

        input.addEventListener("change", (e) => {

            if (e.target.files.length) {
                setLoadingVideo(true);
                createVideo(e.target.files[0], article.id, setProgressVideo).then(response => {
                    setLoadingVideo(false);
                    getVideos(article.id).then(response => setVideos(response.data));
                });
            };

            input.remove();
        });

        document.body.appendChild(input);
        input.click();
    };

    // удаляет файл
    function delFile(file_id) {
        deleteFile(file_id).then(response => {
            getFiles(article.id).then(response => setFiles(response.data));
        });
    };

    // загружает файл
    function addFile() {
        document.querySelectorAll('.input-file-file-hidden').forEach(item => item.remove());

        const input = document.createElement('input');
        input.classList.add('input-file-file-hidden');
        input.hidden = true;
        input.type = 'file';

        input.addEventListener("change", (e) => {

            if (e.target.files.length) {
                setLoadingFiles(true);
                createFile(e.target.files[0], article.id, setProgressFiles).then(response => {
                    setLoadingFiles(false);
                    getFiles(article.id).then(response => setFiles(response.data));
                });
            };

            input.remove();
        });

        document.body.appendChild(input);
        input.click();
    };

    // удаляет аудио
    function delAudio(audio_id) {
        deleteAudio(audio_id).then(response => {
            getAudios(article.id).then(response => setAudios(response.data));
        });
    };

    // загружает аудио
    function addAudio() {
        document.querySelectorAll('.input-file-audio-hidden').forEach(item => item.remove());

        const input = document.createElement('input');
        input.classList.add('input-file-audio-hidden');
        input.accept = 'audio/*';
        input.hidden = true;
        input.type = 'file';

        input.addEventListener("change", (e) => {

            if (e.target.files.length) {
                setLoadingAudio(true);
                createAudio(e.target.files[0], article.id, setProgressAudio).then(response => {
                    setLoadingAudio(false);
                    getAudios(article.id).then(response => setAudios(response.data));
                });
            };

            input.remove();
        });

        document.body.appendChild(input);
        input.click();
    };

    // подставляет иконку под файл
    function getIcon(fileName) {

        const fileExtension = fileName.split('.').pop().toLowerCase();

        const icons = {
            'txt': "data:image/png;base64,",
            'doc': "data:image/png;base64,",
            'docx': "data:image/png;base64,",
            'xls': "data:image/png;base64,",
            'xlsx': "data:image/png;base64,",
            'pdf': "data:image/png;base64,",
        };

        return icons[fileExtension] || iconOther;

    };

    useEffect(() => {

        cancelEditName();
        cancelEditContent();

        if (article) {
            getAudios(article.id).then(response => setAudios(response.data));
            getVideos(article.id).then(response => setVideos(response.data));
            getFiles(article.id).then(response => setFiles(response.data));
        };

    }, [article]);

    return <>

        <div className={style.title}>

            {
                admin && editName &&
                <div className={style.icons}>
                    <img onClick={cancelEditName} className={style.icon} src={iconCancel} />
                    <img onClick={saveEditName} className={style.icon} src={iconOk} />
                </div>
            }

            {
                admin && !editName &&
                <img onClick={() => { setEditName(true) }} className={style.icon} src={iconEdit} />
            }

            {
                !editName
                    ?
                    <section className={style.subContent} dangerouslySetInnerHTML={{ __html: name }} />
                    :
                    <MyEditor v={name} setV={setName} />
            }

        </div>

        <div className={style.content}>

            {
                admin && editContent &&
                <div className={style.icons}>
                    <img onClick={cancelEditContent} className={style.icon} src={iconCancel} />
                    <img onClick={saveEditContent} className={style.icon} src={iconOk} />
                </div>
            }

            {
                admin && !editContent &&
                <img onClick={() => { setEditContent(true) }} className={style.icon} src={iconEdit} />
            }

            {
                !editContent
                    ?
                    <section className={style.subContent} dangerouslySetInnerHTML={{ __html: content }} />
                    :
                    <MyEditor v={content} setV={setContent} />
            }

        </div>

        <div className={style.fileContainer}>
            {
                admin &&
                <div className={style.subTitle}>
                    <img onClick={addVideo} className={style.icon} src={iconAdd} />
                    <div>Видео</div>
                    {loadingVideo && <Loader progress={progressVideo} />}
                </div>
            }

            {
                videos.length > 0 &&
                <>
                    <div className={style.videos}>
                        {
                            videos.map(item => (
                                <div className={style.videoContainer}>
                                    <div className={style.subTitle}>
                                        <div className={style.subTitleName}>{item.name.split('.').slice(0, -1).join('.')}</div>

                                        {
                                            admin &&
                                            <div className={style.iconsFile}>
                                                <img onClick={() => { delVideo(item.id) }} className={style.icon} src={iconCancel} />
                                            </div>
                                        }

                                    </div>
                                    <video width="100%" src={item.file} controls preload="preload"></video>
                                </div>
                            ))
                        }
                    </div>
                </>
            }
        </div>

        <div className={style.fileContainer}>
            {
                admin &&

                <div className={style.subTitle}>
                    <img onClick={addAudio} className={style.icon} src={iconAdd} />
                    <div>Аудио</div>
                    {loadingAudio && <Loader progress={progressAudio} />}
                </div>
            }

            {
                audios.length > 0 &&
                <>
                    <div className={style.audios}>
                        {
                            audios.map(item => (
                                <div className={style.audioContainer}>
                                    <div className={style.audioName}>
                                        <div>{item.name}</div>
                                        {admin && <img onClick={() => { delAudio(item.id) }} className={style.icon} src={iconCancel} />}
                                    </div>
                                    <audio controls>
                                        <source src={item.file} />
                                    </audio>
                                </div>
                            ))
                        }
                    </div>
                </>
            }
        </div>

        <div className={style.fileContainer}>
            {
                admin &&
                <div className={style.subTitle}>
                    <img onClick={addFile} className={style.icon} src={iconAdd} />
                    <div>Файлы</div>
                    {loadingFiles && <Loader progress={progressFiles} />}
                </div>
            }

            {
                files.length > 0 &&
                <>
                    <div className={style.files}>
                        {
                            files.map(item => (
                                <div className={style.file}>
                                    <img src={getIcon(item.name)} />
                                    <a href={item.file} target="_blank" className={style.fileName}>{item.name}</a>
                                    {admin && <img onClick={() => { delFile(item.id) }} className={style.icon} src={iconCancel} />}
                                </div>
                            ))
                        }
                    </div>
                </>
            }
        </div>

        <div className={style.questions}>

        </div>

    </>
};


export { Article }
